import React, { } from "react";
import axios from 'axios';
import { Button, Modal ,Card} from 'react-bootstrap';

import Popup from 'reactjs-popup';




export default class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {

      list: [],
      value: '',
      details: '',
      show: false

    }
    this.handleSubmit = this.handleSubmit.bind(this);
    this.showDetails = this.showDetails.bind(this);

  }
  handleSubmit(event) {
    event.preventDefault();
    this.setState({ value: this.element.value });


    var key = this.state.value
    axios.get('http://www.omdbapi.com/?apikey=4d80ce0a&s=' + key)
      .then((res) => {
        if (res.data.Response) {
          console.log(res.data.Search, 'totaldata')
          if (res.data.Search && res.data.Search.length)
            this.setState({
              list: res.data.Search,

            })
        }
      })

  }


  showDetails(imdbID) {
    console.log(imdbID, "msg")

    axios.get('http://www.omdbapi.com/?apikey=4d80ce0a&i=' + imdbID)
      .then((res) => {

        this.setState({
          details: res.data,
          show: !this.state.show

        })

      })

  }




  handelModel() {
    this.setState({ show: !this.state.show })

  }



  render() {
    const {
      Title,
      Released,
      Genre,
      Plot,
      Actors



    } = this.state.details;


















    return (
      <div>
<Card style={{ width: '18rem' }}>
  <Card.Img variant="top" src="holder.js/100px180" />
  <Card.Body>
    <Card.Title>Card Title</Card.Title>
    <Card.Text>
      Some quick example text to build on the card title and make up the bulk of
      the card's content.
    </Card.Text>
    <Button variant="primary">Go somewhere</Button>
  </Card.Body>
</Card>

        
        




     

        <form >
          <input
            placeholder="Search for a movie"

            type="text" ref={el => this.element = el} />

          <button type="button" onClick={this.handleSubmit}>
            <i class="fa fa-search"></i>
          </button>
        </form>




        <div className="movie-card-container">




          <div >

            {
              this.state.list.map((item) => {
                return (

                  <tr>
                    <img src={item.Poster} className="image" alt="no poster"></img>
                    <td> <h3><b>Name:</b>{item.Title}<br />
                      <b>Year:</b>  {item.Year}<br />

                      <b>Type:</b> {item.Type}<br />
                      <b>Imdbid:</b>  {item.imdbID}<br />

                    </h3>
                      <br />
                      <Button variant="primary" className="bt"
                        onClick={() => { this.showDetails(item.imdbID) }
                        }>  Details</Button>

                    </td>
                  </tr>

                )

              })
            }

          </div>
        </div>

        <div className="App">
          <header>

            <Modal show={this.state.show}>
              <Modal.Header>Movie deatails</Modal.Header>
              <Modal.Body>

                <h2>{Title} </h2>

                <h3> {Released}</h3>
                <h3>    {Genre} </h3>
                <h3>{Actors}</h3>
                <p>{Plot}</p>

              </Modal.Body>
              <Modal.Footer>
                <Button variant="danger" onClick={() => { this.handelModel() }}>
                  close
         </Button>

              </Modal.Footer>
            </Modal>
          </header>
        </div>

















      </div>
    );
  }
}
