import React, { } from "react";
import axios from 'axios';
import { Button, Modal } from 'react-bootstrap';

import Popup from 'reactjs-popup';




export default class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {

      list: [],
      value: '',
      details: '',
      show: false

    }
    this.handleSubmit = this.handleSubmit.bind(this);
    this.showDetails = this.showDetails.bind(this);

  }
  handleSubmit(event) {
    event.preventDefault();
    this.setState({ value: this.element.value });


    var key = this.state.value
    axios.get('http://www.omdbapi.com/?apikey=4d80ce0a&s=' + key)
      .then((res) => {
        if (res.data.Response) {
          console.log(res.data.Search, 'totaldata')
          if (res.data.Search && res.data.Search.length)
            this.setState({
              list: res.data.Search,

            })
        }
      })

  }


  showDetails(imdbID) {
    console.log(imdbID, "msg")

    axios.get('http://www.omdbapi.com/?apikey=4d80ce0a&i=' + imdbID)
      .then((res) => {

        this.setState({
          details: res.data,
          show: !this.state.show

        })

      })

  }




  handelModel() {
    this.setState({ show: !this.state.show })

  }



  render() {
    const {
      Title,
      Released,
      Genre,
      Plot,

      imdbRating
    } = this.state.details;


















    return (

      <div>

        <form onSubmit={this.handleSubmit}>
          <input
            placeholder="Search for a movie"

            type="text" ref={el => this.element = el} />

          <button type="submit">
            <i class="fa fa-search"></i>
          </button>
        </form>




        <div className="movie-card-container">




          <div >

            {
              this.state.list.map((item) => {
                return (
                  <tr>
                    <img src={item.Poster} className="image" alt="no poster"></img>
                    <td> <h3><b>Name:</b>{item.Title}<br />
                      <b>Year:</b>  {item.Year}<br />

                      <b>Type:</b> {item.Type}<br />
                      <b>Imdbid:</b>  {item.imdbID}<br />

                    </h3>
                      <br />
                      <button className="bt"
                        onClick={() => { this.showDetails(item.imdbID) } 
                        }>  Details</button>

                    </td>
                  </tr>

                )

              })
            }

          </div>

        </div>

        <div>
          <div className="App">
            <header>
            
              <Modal show={this.state.show}>
                <Modal.Header>Movie deatails</Modal.Header>
                <Modal.Body>
               
 <h1>{Title} </h1>

<h1> {Released}</h1>
       </Modal.Body>
                <Modal.Footer>
                  <Button onClick={() => { this.handelModel() }}>
                    close modal
         </Button>

                </Modal.Footer>
              </Modal>
            </header>
          </div>
        </div>













        <h1> {Released}</h1>
        <h1> {Released}</h1>
        {Genre}
        {Plot}


      </div>
    );
  }
}
