import React, { } from "react";
import axios from 'axios';

export default class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {

      list: [],
      value: '',
      details:'' ,

    }
    this.handleSubmit = this.handleSubmit.bind(this);
    this.showDetails = this.showDetails.bind(this);

  }
  handleSubmit(event) {
    event.preventDefault();
    this.setState({ value: this.element.value });


    var key = this.state.value
    axios.get('http://www.omdbapi.com/?apikey=4d80ce0a&s=' + key)
      .then((res) => {
        if (res.data.Response) {
          console.log(res.data.Search, 'totaldata')
          if (res.data.Search && res.data.Search.length)
            this.setState({
              list: res.data.Search,

            })
        }
      })

  }

  // search(key) {



  //   fetch("http://www.omdbapi.com/?apikey=4d80ce0a&s=${this.state.b}").then((data) => {
  //     data.json().then((result) => {
  //       //  console.warn(result)
  //         this.setState({list:result
  //       })


  //     });
  //   });
  // }

  // searchs(key) {


  //   fetch("http://www.omdbapi.com/?apikey=4d80ce0a&s=${this.state.b}").then((data) => {
  //     data.json().then((result) => {
  //       //  console.warn(result)
  //         this.setState({
  //         movie:result})


  //     });
  //   });
  // }

  showDetails(imdbID) {
    console.log(imdbID, "msg")

    axios.get('http://www.omdbapi.com/?apikey=4d80ce0a&i=' + imdbID)
      .then((res) => {

        this.setState({
          details: res.data

        })

      })

  }


  render() {
    const {
      Title,
      Released,
      Genre,
      Plot,
    
      imdbRating
  } = this.state.details;

















    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            <input type="text" ref={el => this.element = el} />
          </label>
          <input type="submit" value="Submit" />
        </form>


        <div>


          <ul>
            {
              this.state.list.map((item) => {

                return (
                  <tr >
                    <td>{item.Title}</td>
                    <td>{item.Type}</td>
                    <td>{item.Year}</td>
                    <td>{item.imdbID}</td>
                    <td><button onClick={() => { this.showDetails(item.imdbID) }
                    }>  click</button></td>

                  </tr>
                )

              })
            }
          </ul>



        </div>
        <div>
          {/* <ul>
            {
              this.state.details.map((item) => {

                return (
                  <tr >
                    <td>{this.item.Actors}</td>


                  </tr>
                )

              })
            }
          </ul> */}



        </div>
        <div>
        <h1>{Title}</h1>
        <h1>Released</h1> 
      Genre,  
      Genre,
      Plot,
        

        
        

        </div>

      </div>
    );
  }
}
