import React, { } from "react";
import axios from 'axios';
import { Button ,Card} from 'react-bootstrap';


export default class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {

      list: [],
      value: '',
      details:'' ,

    }
    this.handleSubmit = this.handleSubmit.bind(this);
    this.showDetails = this.showDetails.bind(this);

  }
  handleSubmit(event) {
    event.preventDefault();
    this.setState({ value: this.element.value });


    var key = this.state.value
    axios.get('http://www.omdbapi.com/?apikey=4d80ce0a&s=' + key)
      .then((res) => {
        if (res.data.Response) {
          console.log(res.data.Search, 'totaldata')
          if (res.data.Search && res.data.Search.length)
            this.setState({
              list: res.data.Search,

            })
        }
      })

  }

 
  showDetails(imdbID) {
    console.log(imdbID, "msg")

    axios.get('http://www.omdbapi.com/?apikey=4d80ce0a&i=' + imdbID)
      .then((res) => {

        this.setState({
          details: res.data

        })

      })

  }


  render() {
    const {
      Title,
      Released,
      Genre,
      Plot,
    
      imdbRating
  } = this.state.details;

















    return (
      <div>
      
            <input type="text" ref={el => this.element = el} />
      
          <button type="submit" onClick={this.handleSubmit}
          value="Submit" > Detai </button>
      

        <div>
                                                                                                                                                          

      
            {
              this.state.list.map((item) => {

                return (
                  <tr >
                    <td>{item.Title}</td>
                    <td>{item.Type}</td>
                    <td>{item.Year}</td>
                    <td>{item.imdbID}</td>
                    <td><button onClick={() => { this.showDetails(item.imdbID) }
                    }>  click</button></td>

                  </tr>


                  

                  
                )

              })
            }
          


        </div>
     
        
        <div>
        <h1>{Title}</h1>
        <h1>{Released}</h1> 
        <h1> {Genre}</h1> 
 
        <h3> {Plot }</h3> 
      
        </div>

      </div>
    );
  }
}
