  // }
  showDetails(imdbID) {
    console.log(imdbID, "msg")

  }


  render() {

    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            <input type="text" ref={el => this.element = el} />
          </label>
          <input type="submit" value="Submit" />
        </form>


        <div>


          <ul>
            {
              this.state.list.map((item) => {


                <li >
                  {item.Title}
                </li>


              })
            }
          </ul>



        </div>

      </div>
    );
  }
}
