import React, { } from "react";
import axios from 'axios';

import Popup from 'reactjs-popup';
import '~bootstrap/scss/bootstrap.scss';



export default class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {

      list: [],
      value: '',
      details: '',

    }
    this.handleSubmit = this.handleSubmit.bind(this);
    this.showDetails = this.showDetails.bind(this);

  }
  handleSubmit(event) {
    event.preventDefault();
    this.setState({ value: this.element.value });


    var key = this.state.value
    axios.get('http://www.omdbapi.com/?apikey=4d80ce0a&s=' + key)
      .then((res) => {
        if (res.data.Response) {
          console.log(res.data.Search, 'totaldata')
          if (res.data.Search && res.data.Search.length)
            this.setState({
              list: res.data.Search,

            })
        }
      })

  }


  showDetails(imdbID) {
    console.log(imdbID, "msg")

    axios.get('http://www.omdbapi.com/?apikey=4d80ce0a&i=' + imdbID)
      .then((res) => {

        this.setState({
          details: res.data

        })

      })

  }
  


  render() {
    const {
      Title,
      Released,
      Genre,
      Plot,

      imdbRating
    } = this.state.details;



















    return (

      <div>

        <form onSubmit={this.handleSubmit}>
          <input
            placeholder="Search for a movie"

            type="text" ref={el => this.element = el} />

          <button type="submit">
            <i class="fa fa-search"></i>
          </button>
        </form>




        <div className="movie-card-container">




          <div >

            {
              this.state.list.map((item) => {
                return (





                  <tr>
                    <img src={item.Poster} className="image" alt="no poster"></img>
                    <td> <h3><b>Name:</b>{item.Title}<br />
                    <b>Year:</b>  {item.Year}<br />

                     <b>Type:</b> {item.Type}<br /> 
                    <b>Imdbid:</b>  {item.imdbID}<br />

                    </h3>
                    <br/>
                    <button className="bt" 
                    onClick={() => { this.showDetails(item.imdbID)} 
                    }>  Details</button>


                    <Popup   classname="pop"trigger={  <button className="bt" 
                    onClick={() => { this.showDetails(item.imdbID) }
                    }>  Details</button>
} position="right bottom">
    <div>
      Name:{Title}<br/>
          Released:{Released}<br/>
           Genre:{Genre}<br/>
           Plot:{Plot}<br/>


</div>
  </Popup> 
  <Popup trigger={<button>Trigger</button>} position="top left">
    {close => (
      <div>
        Content here
        <a className="close" onClick={close}>
          &times;
        </a>
      </div>
    )}
  </Popup>



  
{
  
}




                  </td>
                  </tr>

                )

              })
            }

          </div>


        </div>
        <button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
  Launch demo modal
</button>
      
        <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>

      </div>
    );
  }
}
