import React, { } from "react";
import axios from 'axios';
import { Card } from 'react-bootstrap';


export default class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {

      list: [],
      value: '',
      details: '',

    }
    this.handleSubmit = this.handleSubmit.bind(this);
    this.showDetails = this.showDetails.bind(this);

  }
  handleSubmit(event) {
    event.preventDefault();
    this.setState({ value: this.element.value });


    var key = this.state.value
    axios.get('http://www.omdbapi.com/?apikey=4d80ce0a&s=' + key)
      .then((res) => {
        if (res.data.Response) {
          console.log(res.data.Search, 'totaldata')
          if (res.data.Search && res.data.Search.length)
            this.setState({
              list: res.data.Search,

            })
        }
      })

  }


  showDetails(imdbID) {
    console.log(imdbID, "msg")

    axios.get('http://www.omdbapi.com/?apikey=4d80ce0a&i=' + imdbID)
      .then((res) => {

        this.setState({
          details: res.data

        })

      })

  }


  render() {
    const {
      Title,
      Released,
      Genre,
      Plot,

      imdbRating
    } = this.state.details;

















    return (

      <div>

        <form onSubmit={this.handleSubmit}>
          <input
            placeholder="Search for a movie"

            type="text" ref={el => this.element = el} />

          <button type="submit">
            <i class="fa fa-search"></i>
          </button>
        </form>




        {/* <input type="text" ref={el => this.element = el} />
      
          <button type="submit" onClick={this.handleSubmit}
          value="Submit" > Detai </button>
       */}
<div className="movie-card-container">
          <div className="image-container">

          <div className="movie-info">
            

            {
              this.state.list.map((item) => {
                return (
                <div
                className="bg-image"
                style={{ backgroundImage: `url(${item.Poster})` }}
            >
        

               
                  
                  <li >
                    <p>{item.Title}</p>
                    <p>{item.Type}</p>
                    <p>{item.Year}</p>
                    <p>{item.imdbID}</p>
                    <p><button onClick={() => { this.showDetails(item.imdbID) }
                    }>  click</button></p>

                  </li>

</div>
                )

              })
            }



             </div> 
          </div>
          </div>
      



          <div>
            <h1>{Title}</h1>
            <h1>{Released}</h1>
            <h1> {Genre}</h1>

            <h3> {Plot}</h3>





          </div>

        </div>
    );
  }
}
